import React, { useState } from "react";
import {useNavigate} from 'react-router-dom'
import "./allpages.css";
import axios from "axios";
function UserCreate() {
  let [name, setname] = useState();
  let [age, setage] = useState();
  let [city, setcity] = useState();
  let [email, setemail] = useState();

  let navigate = useNavigate()

  let usersubmit=(e)=>{
    e.preventDefault()
    axios.post("http://localhost:3000/User",{
      name,age,city,email
    }).then((res)=>{
console.log(res.data);
navigate("/read")
    })
  }

  return (
    <>
    <form>
     <div class="mb-3">
  <label  class="form-label">Name-:</label>
  <input type="text" class="form-control" placeholder="enter name" onChange={(e)=>{this.setState({name:e.target.value})}}/>
</div>
<div class="mb-3">
  <label  class="form-label">Email-:</label>
  <input type="email" class="form-control" placeholder="enter email" onChange={(e)=>{this.setState({email:e.target.value})}}/>
</div>
<div class="mb-3">
  <label  class="form-label">city-:</label>
  <input type="text" class="form-control" placeholder="enter city" onChange={(e)=>{this.setState({city:e.target.value})}}/>
</div>
<div class="mb-3">
  <label  class="form-label">Age-:</label>
  <input type="number" class="form-control" placeholder="enter Age" onChange={(e)=>{this.setState({age:e.target.value})}}/>
</div>
<div class="mb-3">
  <input type="Submit" class="form-control bg-danger" onClick={this.submit} />
</div>
     </form>
     
    </>
  );
}

export default UserCreate;
