import React,{useReducer} from 'react'

const reducer =(state,action)=>{
if(action.type==='int'){
  state=state-1
}if(action.t==='des'){
  state=state-1
}
return state
}

export default function App() {

  let instatevalue=13

let[state,dsipatch]=useReducer(reducer,instatevalue)


  return (
    <>
     <button onClick={()=>{dsipatch({type:"int"})}}>+</button> 
     <span>{state}</span>
     <button onClick={()=>{dsipatch({type:"des"})}}>-</button>
    </>
  )
}
