import React,{useEffect,useState} from 'react'
import {useParams,useNavigate} from 'react-router-dom'
import axios from 'axios';


function UserUpdate() {

let navigate = useNavigate()
  let {id}=useParams()

let[name,setname]=useState()
let[age,setage]=useState()
let[city,setcity]=useState()
let[email,setemail]=useState()

  useEffect(()=>{
    axios.get("http://localhost:3000/User/" + id).then((res)=>{
setname(res.data.name)
setage(res.data.age)
setcity(res.data.city)
setemail(res.data.email)


    })
  },[id])


let updateuser=(e)=>{
  e.preventDefault()
  axios.put("http://localhost:3000/User/"+id,{
    name,age,city,email
  }).then(()=>{
navigate=("/read")
  })
}

  return (
    <>
   
   <div className="myform">
        <h2 className="myheading">Update User</h2>
        <form method="post" onSubmit={updateuser}>
        <div className="importdata">
          <label>Name</label>
          <input type="text" name="name" placeholder="Enter your name" value={name} onChange={(e)=>{setname(e.target.value)}}/>
        </div>
        <div className="importdata">
          <label>Age</label>
          <input type="Number" name="age" placeholder="Enter your Age" value={age} onChange={(e)=>{setage(e.target.value)}}/>
        </div>

        <div className="importdata">
          <label>City</label>
          <input type="text" name="city" placeholder="Enter your City" value={city} onChange={(e)=>{setcity(e.target.value)}} />
        </div>

        <div className="importdata">
          <label>Email-Id</label>
          <input type="email" name="email" placeholder="Enter your Email-Id" value={email}onChange={(e)=>{setemail(e.target.value)}} />
        </div>

        <div className="importdata">
          <input type="submit" name="sub" value='update'/>
        </div>
        </form>
      </div>
    </>
  )
}

export default UserUpdate
